    <script language="javascript">
      function popup(url, titre, largeur, hauteur, scroll) {
        window.open(url, titre, 'width=' + largeur + ', height=' + hauteur + ', scrollbars=' + scroll + ',menubar=no,resizable=no,status=no,toolbar=no');
      }
      
	  function Zoom(article) {
      	
		if(article == 'taille')
      	  window.open('http://www.3suisses.be/service/FR/assistant_taille.htm','zoom','fullscreen=no,channelmode=no,toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=yes,resizable=no,width=650,height=600,top=100,left=100');
      	
		else if(article == 'basket') {
					  var basket = window.open('/engine/basket.php?lng=FR','zoom','fullscreen=no,channelmode=no,toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=yes,resizable=no,width=420,height=500,top=100,left=100');
					  basket.focus();
		}
		
		else if(article == 'password')
			window.open('/engine/forget_passwd.php','zoom','fullscreen=no,channelmode=no,toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=yes,resizable=no,width=420,height=250,top=100,left=100');
      	
		else if(article == 'partnerCard')
			window.open('/discount/s00_partner/FR/index.asp', 'zoom', 'fullscreen=no,channelmode=no,toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=yes,resizable=no,width=600,height=300,top=100,left=100');
		
		else  
      	  window.open('/engine/zoom.php?id=' + article,'zoom','fullscreen=no,channelmode=no,toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=no,resizable=no,width=415,height=415,top=100,left=100');
      }
	  
	  function RemoveIt() {
		  subscribeForm.accEmail.value = "";
	  }
	  
	  var bookmarkurl="http://www.3suisses.be";
	  var bookmarktitle="3SUISSES Belgique";
		function AjoutFavoris()
			{
			if (document.all)
			window.external.AddFavorite(bookmarkurl,bookmarktitle);
			}


      function RefreshArticle() {
      	form = document.forms.articles; 
      	if((form.ACOL.options.length == 1 || form.ACOL.value != -1) && (form.ASIZ.options.length == 1 || form.ASIZ.value != -1) && form.AQTY.value != 0)
      	  window.location = '/[ART_NAME_PROPER]-a[ART_REF]' + form.ACOL.value + form.ASIZ.value + form.AQTY.value + '-FR.html';
      }

      function AddToBasket(ref) {
      	form = document.forms.articles; 
      	if((form.ACOL.options.length == 1 || form.ACOL.value != -1) && (form.ASIZ.options.length == 1 || form.ASIZ.value != -1) && form.AQTY.value != 0) {
      	  //if(form.ACOL.options.length == 1) form.ACOL.value = '0';
      	  //if(form.ASIZ.options.length == 1) form.ASIZ.value = 'XXX';
      	  //form.submit();
      	  //document.location = '/engine/basket.php?action=set&id=' + ref + '&nb=' + form.AQTY.value;
					window.open('/engine/basket.php?action=set&id=' + ref + '&nb=' + form.AQTY.value + '&PHPSESSID=[PHPSESSID]', 'zoom', 'fullscreen=no,channelmode=no,toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=yes,resizable=no,width=420,height=500,top=100,left=100');
      	}
      	else
      	  alert('Merci de sélectionner un coloris, une taille et une quantité avant de poursuivre.');
      }
      
      function menuChange() {
      	form = document.forms.clientMenu; 
      	url = form.clientOptions.options[form.clientOptions.options.selectedIndex].value;
      	if(url != '') window.location = url;
      }
      
      function searchArticle() {
      	searchBox = document.forms.search.searchField;
      	searchBox.value = searchBox.value.replace(/(^\s*)|(\s*$)/g, '');
      	searchBox.value = searchBox.value.replace(/(\s+)/g, '+');
      	searchBox.value = searchBox.value.replace(/[+]{2,}/g, '+');
      	document.forms.search.submit();
      }
    </script>

    
